contracts/system_tests/TestTokenFlow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import './helpers/TestBaseWorkflow.sol';

/// @notice This file tests JBToken related flows
contract TestTokenFlow is TestBaseWorkflow {
  JBController private _controller;
  JBTokenStore private _tokenStore;

  JBProjectMetadata private _projectMetadata;
  JBFundingCycleData private _data;
  JBFundingCycleMetadata private _metadata;
  JBGroupedSplits[] private _groupedSplits; // Default empty
  JBFundAccessConstraints[] private _fundAccessConstraints; // Default empty
  IJBPaymentTerminal[] private _terminals; // Default empty

  uint256 private _projectId;
  address private _projectOwner;
  uint256 private _reservedRate = 5000;

  function setUp() public override {
    super.setUp();

    _controller = jbController();
    _tokenStore = jbTokenStore();

    _projectMetadata = JBProjectMetadata({content: 'myIPFSHash', domain: 1});

    _data = JBFundingCycleData({
      duration: 14,
      weight: 1000 * 10**18,
      discountRate: 450000000,
      ballot: IJBFundingCycleBallot(address(0))
    });

    _metadata = JBFundingCycleMetadata({
      global: JBGlobalFundingCycleMetadata({allowSetTerminals: false, allowSetController: false}),
      reservedRate: _reservedRate,
      redemptionRate: 5000, //50%
      ballotRedemptionRate: 0,
      pausePay: false,
      pauseDistributions: false,
      pauseRedeem: false,
      pauseBurn: false,
      allowMinting: true,
      allowChangeToken: true,
      allowTerminalMigration: false,
      allowControllerMigration: false,
      holdFees: false,
      useTotalOverflowForRedemptions: false,
      useDataSourceForPay: false,
      useDataSourceForRedeem: false,
      dataSource: address(0)
    });

    _projectOwner = multisig();

    _projectId = _controller.launchProjectFor(
      _projectOwner,
      _projectMetadata,
      _data,
      _metadata,
      block.timestamp,
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );
  }

  /**
   * @notice tests the following flow with fuzzed values:
   * launch project → issue token → change token → mint token → burn token
   */
  function testFuzzTokenFlow(
    uint256 mintAmount,
    uint256 burnAmount,
    bool mintPreferClaimed,
    bool burnPreferClaimed
  ) public {
    // calls will originate from projectOwner addr
    evm.startPrank(_projectOwner);

    // issue an ERC-20 token for project
    _controller.issueTokenFor(_projectId, 'TestName', 'TestSymbol');

    // create a new IJBToken and change it's owner to the tokenStore
    IJBToken _newToken = new JBToken('NewTestName', 'NewTestSymbol');
    _newToken.transferOwnership(_projectId, address(_tokenStore));

    // change the projects token to _newToken
    _controller.changeTokenOf(_projectId, _newToken, address(0));

    // confirm the project's new JBToken
    assertEq(address(_tokenStore.tokenOf(_projectId)), address(_newToken));

    address _beneficiary = address(1234);
    uint256 _expectedTokenBalance = 0;
    uint256 _beneficiaryTokenAmount = mintAmount / 2; // 50% reserved rate results in half the mintAmount

    if (mintAmount == 0) evm.expectRevert(abi.encodeWithSignature('ZERO_TOKENS_TO_MINT()'));
    else _expectedTokenBalance = _beneficiaryTokenAmount;

    // mint tokens to beneficiary addr
    _controller.mintTokensOf(
      _projectId,
      mintAmount,
      _beneficiary,
      'Mint memo',
      mintPreferClaimed,
      true /*use reserved rate*/
    );

    // total token balance should be half of token count due to 50% reserved rate
    assertEq(_tokenStore.balanceOf(_beneficiary, _projectId), _expectedTokenBalance);

    if (burnAmount == 0) evm.expectRevert(abi.encodeWithSignature('NO_BURNABLE_TOKENS()'));
    else if (burnAmount > _expectedTokenBalance)
      evm.expectRevert(abi.encodeWithSignature('INSUFFICIENT_FUNDS()'));
    else _expectedTokenBalance = _expectedTokenBalance - burnAmount;

    // burn tokens from beneficiary addr
    // next call will originate from holder addr
    evm.stopPrank();
    evm.prank(_beneficiary);
    _controller.burnTokensOf(
      _beneficiary,
      _projectId,
      /* _tokenCount */
      burnAmount,
      'Burn memo',
      burnPreferClaimed
    );

    // total balance of tokens should be updated
    assertEq(_tokenStore.balanceOf(_beneficiary, _projectId), _expectedTokenBalance);
  }

  /**
   * @notice tests the following corner case:
   * launch project → issue token → mint max claimed tokens → mint max unclaimed tokens → try to claim unclaimed tokens
   */
  function testLargeTokenClaimFlow() public {
    // calls will originate from projectOwner addr
    evm.startPrank(_projectOwner);

    // issue an ERC-20 token for project
    _controller.issueTokenFor(_projectId, 'TestName', 'TestSymbol');

    address _beneficiary = address(1234);

    // mint claimed tokens to beneficiary addr
    _controller.mintTokensOf(
      _projectId,
      type(uint224).max,
      _beneficiary,
      'Mint memo',
      true,
      false /*use reserved rate*/
    );

    // mint unclaimed tokens to beneficiary addr
    _controller.mintTokensOf(
      _projectId,
      type(uint256).max,
      _beneficiary,
      'Mint memo',
      false,
      false
    );

    // try to claim the unclaimed tokens
    evm.stopPrank();
    evm.prank(_beneficiary);
    _tokenStore.claimFor(
      _beneficiary,
      _projectId,
      /* _amount */
      1
    );
  }

  /**
   * @notice tests the following corner case:
   * launch project → issue token → mint unclaimed tokens → switch to new token → claim unclaimed tokens of the new token
   */
  function testTokenChangeFlow() public {
    // calls will originate from projectOwner addr
    evm.startPrank(_projectOwner);

    // issue an ERC-20 token for project
    _controller.issueTokenFor(_projectId, 'TestName', 'TestSymbol');

    address _beneficiary = address(1234);

    // mint unclaimed tokens to beneficiary addr
    _controller.mintTokensOf(
      _projectId,
      type(uint256).max,
      _beneficiary,
      'Mint memo',
      false,
      false
    );

    // create a new IJBToken and change it's owner to the tokenStore
    IJBToken _newToken = new JBToken('NewTestName', 'NewTestSymbol');
    _newToken.transferOwnership(_projectId, address(_tokenStore));

    // change the projects token to _newToken
    _controller.changeTokenOf(_projectId, _newToken, address(0));

    // claim and mint the max possible amount of unclaimed tokens
    evm.stopPrank();
    evm.prank(_beneficiary);
    _tokenStore.claimFor(_beneficiary, _projectId, type(uint224).max);

    // total token balanced should be updated
    assertEq(_newToken.balanceOf(_beneficiary, _projectId), type(uint224).max);
    assertEq(
      _tokenStore.unclaimedBalanceOf(_beneficiary, _projectId),
      type(uint256).max - type(uint224).max
    );
    assertEq(_tokenStore.unclaimedTotalSupplyOf(_projectId), type(uint256).max - type(uint224).max);
    assertEq(_tokenStore.balanceOf(_beneficiary, _projectId), type(uint256).max);
  }
}

contracts/system_tests/TestReconfigure.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import './helpers/TestBaseWorkflow.sol';

import '../JBReconfigurationBufferBallot.sol';

uint256 constant WEIGHT = 1000 * 10**18;

contract TestReconfigureProject is TestBaseWorkflow {
  JBController controller;
  JBProjectMetadata _projectMetadata;
  JBFundingCycleData _data;
  JBFundingCycleData _dataReconfiguration;
  JBFundingCycleData _dataWithoutBallot;
  JBFundingCycleMetadata _metadata;
  JBReconfigurationBufferBallot _ballot;
  JBGroupedSplits[] _groupedSplits; // Default empty
  JBFundAccessConstraints[] _fundAccessConstraints; // Default empty
  IJBPaymentTerminal[] _terminals; // Default empty

  uint256 BALLOT_DURATION = 3 days;

  function setUp() public override {
    super.setUp();

    controller = jbController();

    _projectMetadata = JBProjectMetadata({content: 'myIPFSHash', domain: 1});

    _ballot = new JBReconfigurationBufferBallot(BALLOT_DURATION, jbFundingCycleStore());

    _data = JBFundingCycleData({
      duration: 6 days,
      weight: 10000 * 10**18,
      discountRate: 0,
      ballot: _ballot
    });

    _dataWithoutBallot = JBFundingCycleData({
      duration: 6 days,
      weight: 1000 * 10**18,
      discountRate: 0,
      ballot: JBReconfigurationBufferBallot(address(0))
    });

    _dataReconfiguration = JBFundingCycleData({
      duration: 6 days,
      weight: 69 * 10**18,
      discountRate: 0,
      ballot: JBReconfigurationBufferBallot(address(0))
    });

    _metadata = JBFundingCycleMetadata({
      global: JBGlobalFundingCycleMetadata({allowSetTerminals: false, allowSetController: false}),
      reservedRate: 5000,
      redemptionRate: 5000,
      ballotRedemptionRate: 0,
      pausePay: false,
      pauseDistributions: false,
      pauseRedeem: false,
      pauseBurn: false,
      allowMinting: true,
      allowChangeToken: false,
      allowTerminalMigration: false,
      allowControllerMigration: false,
      holdFees: false,
      useTotalOverflowForRedemptions: false,
      useDataSourceForPay: false,
      useDataSourceForRedeem: false,
      dataSource: address(0)
    });

    _terminals = [jbETHPaymentTerminal()];
  }

  function testReconfigureProject() public {
    uint256 projectId = controller.launchProjectFor(
      multisig(),
      _projectMetadata,
      _data,
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );

    JBFundingCycle memory fundingCycle = jbFundingCycleStore().currentOf(projectId);

    assertEq(fundingCycle.number, 1); // ok
    assertEq(fundingCycle.weight, _data.weight);
    emit log_uint(fundingCycle.basedOn);
    emit log_uint(fundingCycle.configuration);

    uint256 currentConfiguration = fundingCycle.configuration;

    evm.warp(block.timestamp + 1); // Avoid overwriting if same timestamp

    evm.prank(multisig());
    controller.reconfigureFundingCyclesOf(
      projectId,
      _data, // 3days ballot
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      ''
    );

    // Shouldn't have changed
    fundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(fundingCycle.number, 1);
    assertEq(fundingCycle.configuration, currentConfiguration);
    assertEq(fundingCycle.weight, _data.weight);

    // should be new funding cycle
    evm.warp(fundingCycle.start + fundingCycle.duration);

    JBFundingCycle memory newFundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(newFundingCycle.number, 2);
    assertEq(newFundingCycle.weight, _data.weight);
  }

  function testMultipleReconfigurationOnRolledOver() public {
    uint256 weightFirstReconfiguration = 1234 * 10**18;
    uint256 weightSecondReconfiguration = 6969 * 10**18;

    uint256 projectId = controller.launchProjectFor(
      multisig(),
      _projectMetadata,
      _data,
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );

    JBFundingCycle memory fundingCycle = jbFundingCycleStore().currentOf(projectId);

    // Initial funding cycle data
    assertEq(fundingCycle.number, 1);
    assertEq(fundingCycle.weight, _data.weight);

    uint256 currentConfiguration = fundingCycle.configuration;

    // Jump to FC+1, rolled over
    evm.warp(block.timestamp + fundingCycle.duration); 

    // First reconfiguration
    evm.prank(multisig());
    controller.reconfigureFundingCyclesOf(
      projectId,
      JBFundingCycleData({
        duration: 6 days,
        weight: weightFirstReconfiguration,
        discountRate: 0,
        ballot: _ballot
      }), // 3days ballot
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      ''
    );

    evm.warp(block.timestamp + 1); // Avoid overwrite

    // Second reconfiguration (different configuration)
    evm.prank(multisig());
    controller.reconfigureFundingCyclesOf(
      projectId,
        JBFundingCycleData({
        duration: 6 days,
        weight: weightSecondReconfiguration,
        discountRate: 0,
        ballot: _ballot
      }), // 3days ballot
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      ''
    );
    uint256 secondReconfiguration = block.timestamp;

    // Shouldn't have changed, still in FC#2, rolled over from FC#1
    fundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(fundingCycle.number, 2);
    assertEq(fundingCycle.configuration, currentConfiguration);
    assertEq(fundingCycle.weight, _data.weight);

    // Jump to after the ballot passed, but before the next FC
    evm.warp(fundingCycle.start + fundingCycle.duration - 1);

    // Queued should be the second reconfiguration
    JBFundingCycle memory queuedFundingCycle = jbFundingCycleStore().queuedOf(projectId);
    assertEq(queuedFundingCycle.number, 3);
    assertEq(queuedFundingCycle.configuration, secondReconfiguration);
    assertEq(queuedFundingCycle.weight, weightSecondReconfiguration);

    evm.warp(fundingCycle.start + fundingCycle.duration);

    // Second reconfiguration should be now the current one
    JBFundingCycle memory newFundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(newFundingCycle.number, 3);
    assertEq(newFundingCycle.configuration, secondReconfiguration);
    assertEq(newFundingCycle.weight, weightSecondReconfiguration);
  }

  function testMultipleReconfigure(uint8 FUZZED_BALLOT_DURATION) public {
    _ballot = new JBReconfigurationBufferBallot(FUZZED_BALLOT_DURATION, jbFundingCycleStore());

    _data = JBFundingCycleData({
      duration: 6 days,
      weight: 10000 ether,
      discountRate: 0,
      ballot: _ballot
    });

    uint256 projectId = controller.launchProjectFor(
      multisig(),
      _projectMetadata,
      _data, // duration 6 days, weight=10k, ballot 3days
      _metadata,
      0, // Start asap
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );

    JBFundingCycle memory initialFundingCycle = jbFundingCycleStore().currentOf(projectId);
    JBFundingCycle memory currentFundingCycle = initialFundingCycle;
    JBFundingCycle memory queuedFundingCycle = jbFundingCycleStore().queuedOf(projectId);

    evm.warp(currentFundingCycle.start + 1); // Avoid overwriting current fc while reconfiguring

    for (uint256 i = 0; i < 4; i++) {
      currentFundingCycle = jbFundingCycleStore().currentOf(projectId);

      if (FUZZED_BALLOT_DURATION + i * 1 days < currentFundingCycle.duration)
        assertEq(currentFundingCycle.weight, initialFundingCycle.weight - i);

      _data = JBFundingCycleData({
        duration: 6 days,
        weight: initialFundingCycle.weight - (i + 1), // i+1 -> next funding cycle
        discountRate: 0,
        ballot: _ballot
      });

      evm.prank(multisig());
      controller.reconfigureFundingCyclesOf(
        projectId,
        _data,
        _metadata,
        0,
        _groupedSplits,
        _fundAccessConstraints,
        ''
      );

      currentFundingCycle = jbFundingCycleStore().currentOf(projectId);
      queuedFundingCycle = jbFundingCycleStore().queuedOf(projectId);

      // While ballot is failed, queued is current rolled over
      assertEq(queuedFundingCycle.weight, currentFundingCycle.weight);
      assertEq(queuedFundingCycle.number, currentFundingCycle.number + 1);

      // Is the full ballot duration included in the funding cycle?
      if (
        FUZZED_BALLOT_DURATION == 0 ||
        currentFundingCycle.duration % (FUZZED_BALLOT_DURATION + i * 1 days) <
        currentFundingCycle.duration
      ) {
        assertEq(currentFundingCycle.weight, initialFundingCycle.weight - i);

        // we shift forward the start of the ballot into the fc, one day at a time, from fc to fc
        evm.warp(currentFundingCycle.start + currentFundingCycle.duration + i * 1 days);

        // ballot should be in Approved state now, queued is the reconfiguration rolled over
        queuedFundingCycle = jbFundingCycleStore().queuedOf(projectId);
        assertEq(queuedFundingCycle.weight, currentFundingCycle.weight - 1);
        assertEq(queuedFundingCycle.number, currentFundingCycle.number + 2);
      }
      // the ballot is accross two funding cycles
      else {
        // Warp to begining of next FC: should be the previous fc config rolled over (ballot is in Failed state)
        evm.warp(currentFundingCycle.start + currentFundingCycle.duration);
        assertEq(currentFundingCycle.weight, initialFundingCycle.weight - i);
        uint256 cycleNumber = currentFundingCycle.number;

        // Warp to after the end of the ballot, within the same fc: should be the new fc (ballot is in Approved state)
        evm.warp(currentFundingCycle.start + currentFundingCycle.duration + FUZZED_BALLOT_DURATION);
        currentFundingCycle = jbFundingCycleStore().currentOf(projectId);
        assertEq(currentFundingCycle.weight, initialFundingCycle.weight - i - 1);
        assertEq(currentFundingCycle.number, cycleNumber + 1);
      }
    }
  }

  function testReconfigureProjectFuzzRates(
    uint96 RESERVED_RATE,
    uint96 REDEMPTION_RATE,
    uint96 BALANCE
  ) public {
    evm.assume(payable(msg.sender).balance / 2 >= BALANCE);
    evm.assume(100 < BALANCE);

    address _beneficiary = address(69420);
    uint256 projectId = controller.launchProjectFor(
      multisig(),
      _projectMetadata,
      _dataWithoutBallot,
      _metadata,
      0, // _mustStartAtOrAfter
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );

    JBFundingCycle memory fundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(fundingCycle.number, 1);

    evm.warp(block.timestamp + 1);

    jbETHPaymentTerminal().pay{value: BALANCE}(
      projectId,
      BALANCE,
      address(0),
      _beneficiary,
      0,
      false,
      'Forge test',
      new bytes(0)
    );

    uint256 _userTokenBalance = PRBMath.mulDiv(BALANCE, (WEIGHT / 10**18), 2); // initial FC rate is 50%
    if (BALANCE != 0)
      assertEq(jbTokenStore().balanceOf(_beneficiary, projectId), _userTokenBalance);

    evm.prank(multisig());
    if (RESERVED_RATE > 10000) evm.expectRevert(abi.encodeWithSignature('INVALID_RESERVED_RATE()'));
    else if (REDEMPTION_RATE > 10000)
      evm.expectRevert(abi.encodeWithSignature('INVALID_REDEMPTION_RATE()'));

    controller.reconfigureFundingCyclesOf(
      projectId,
      _dataWithoutBallot,
      JBFundingCycleMetadata({
        global: JBGlobalFundingCycleMetadata({allowSetTerminals: false, allowSetController: false}),
        reservedRate: RESERVED_RATE,
        redemptionRate: REDEMPTION_RATE,
        ballotRedemptionRate: 0,
        pausePay: false,
        pauseDistributions: false,
        pauseRedeem: false,
        pauseBurn: false,
        allowMinting: true,
        allowChangeToken: false,
        allowTerminalMigration: false,
        allowControllerMigration: false,
        holdFees: false,
        useTotalOverflowForRedemptions: false,
        useDataSourceForPay: false,
        useDataSourceForRedeem: false,
        dataSource: address(0)
      }),
      0,
      _groupedSplits,
      _fundAccessConstraints,
      ''
    );

    if (RESERVED_RATE > 10000 || REDEMPTION_RATE > 10000) {
      REDEMPTION_RATE = 5000; // If reconfigure has reverted, keep previous rates
      RESERVED_RATE = 5000;
    }

    evm.warp(block.timestamp + fundingCycle.duration);

    fundingCycle = jbFundingCycleStore().currentOf(projectId);
    assertEq(fundingCycle.number, 2);

    jbETHPaymentTerminal().pay{value: BALANCE}(
      projectId,
      BALANCE,
      address(0),
      _beneficiary,
      0,
      false,
      'Forge test',
      new bytes(0)
    );

    uint256 _newUserTokenBalance = RESERVED_RATE == 0 // New fc, rate is RESERVED_RATE
      ? PRBMath.mulDiv(BALANCE, WEIGHT, 10**18)
      : PRBMath.mulDiv(PRBMath.mulDiv(BALANCE, WEIGHT, 10**18), 10000 - RESERVED_RATE, 10000);

    if (BALANCE != 0)
      assertEq(
        jbTokenStore().balanceOf(_beneficiary, projectId),
        _userTokenBalance + _newUserTokenBalance
      );

    uint256 tokenBalance = jbTokenStore().balanceOf(_beneficiary, projectId);
    uint256 totalSupply = jbController().totalOutstandingTokensOf(projectId, RESERVED_RATE);
    uint256 overflow = jbETHPaymentTerminal().currentEthOverflowOf(projectId);

    evm.startPrank(_beneficiary);
    jbETHPaymentTerminal().redeemTokensOf(
      _beneficiary,
      projectId,
      tokenBalance,
      address(0), //token (unused)
      0,
      payable(_beneficiary),
      '',
      new bytes(0)
    );
    evm.stopPrank();

    if (BALANCE != 0 && REDEMPTION_RATE != 0)
      assertEq(
        _beneficiary.balance,
        PRBMath.mulDiv(
          PRBMath.mulDiv(overflow, tokenBalance, totalSupply),
          REDEMPTION_RATE + PRBMath.mulDiv(tokenBalance, 10000 - REDEMPTION_RATE, totalSupply),
          10000
        )
      );
  }
}

contracts/JBReconfigurationBufferBallot..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import '@openzeppelin/contracts/utils/introspection/ERC165.sol';
import './interfaces/IJBReconfigurationBufferBallot.sol';
import './structs/JBFundingCycle.sol';

/** 
  @notice 
  Manages approving funding cycle reconfigurations automatically after a buffer period.

  @dev
  Adheres to -
  IJBReconfigurationBufferBallot: General interface for the methods in this contract that interact with the blockchain's state according to the protocol's rules.

  @dev
  Inherits from -
  ERC165: Introspection on interface adherance. 
*/
contract JBReconfigurationBufferBallot is IJBReconfigurationBufferBallot, ERC165 {
  //*********************************************************************//
  // ---------------- public immutable stored properties --------------- //
  //*********************************************************************//

  /**
    @notice 
    The number of seconds that must pass for a funding cycle reconfiguration to become either `Approved` or `Failed`.
  */
  uint256 public immutable override duration;

  /**
    @notice
    The contract storing all funding cycle configurations.
  */
  IJBFundingCycleStore public immutable override fundingCycleStore;

  //*********************************************************************//
  // --------------------- public stored properties -------------------- //
  //*********************************************************************//

  /**
    @notice 
    The finalized state.

    @dev
    If `Active`, the ballot for the provided configuration can still be finalized whenever its state settles.

    _projectId The ID of the project to check the final ballot state of.
    _configuration The configuration of the funding cycle to check the final ballot state of.
  */
  mapping(uint256 => mapping(uint256 => JBBallotState)) public override finalState;

  //*********************************************************************//
  // -------------------------- public views --------------------------- //
  //*********************************************************************//

  /**
    @notice 
    The approval state of a particular funding cycle.

    @param _projectId The ID of the project to which the funding cycle being checked belongs.
    @param _configured The configuration of the funding cycle to check the state of.
    @param _start The start timestamp of the funding cycle to check the state of.

    @return The state of the provided ballot.
  */
  function stateOf(
    uint256 _projectId,
    uint256 _configured,
    uint256 _start
  ) public view override returns (JBBallotState) {
    // If there is a finalized state, return it.
    if (finalState[_projectId][_configured] != JBBallotState.Active)
      return finalState[_projectId][_configured];

    // If the delay hasn't yet passed, the ballot is either failed or active.
    if (block.timestamp < _configured + duration)
      // If the current timestamp is past the start, the ballot is failed.
      return (block.timestamp >= _start) ? JBBallotState.Failed : JBBallotState.Active;

    // The ballot is otherwise approved.
    return JBBallotState.Approved;
  }

  /**
    @notice
    Indicates if this contract adheres to the specified interface.

    @dev 
    See {IERC165-supportsInterface}.

    @param _interfaceId The ID of the interface to check for adherance to.

    @return A flag indicating if this contract adheres to the specified interface.
  */
  function supportsInterface(bytes4 _interfaceId)
    public
    view
    virtual
    override(ERC165, IERC165)
    returns (bool)
  {
    return
      _interfaceId == type(IJBReconfigurationBufferBallot).interfaceId ||
      _interfaceId == type(IJBFundingCycleBallot).interfaceId ||
      super.supportsInterface(_interfaceId);
  }

  //*********************************************************************//
  // -------------------------- constructor ---------------------------- //
  //*********************************************************************//

  /**
    @param _duration The number of seconds to wait until a reconfiguration can be either `Approved` or `Failed`.
    @param _fundingCycleStore A contract storing all funding cycle configurations.
  */
  constructor(uint256 _duration, IJBFundingCycleStore _fundingCycleStore) {
    duration = _duration;
    fundingCycleStore = _fundingCycleStore;
  }

  //*********************************************************************//
  // ---------------------- external transactions ---------------------- //
  //*********************************************************************//

  /**
    @notice 
    Finalizes a configuration state if the current state has settled.

    @param _projectId The ID of the project to which the funding cycle being checked belongs.
    @param _configured The configuration of the funding cycle to check the state of.

    @return ballotState The state of the finalized ballot. If `Active`, the ballot can still later be finalized when it's state resolves.
  */
  function finalize(uint256 _projectId, uint256 _configured)
    external
    override
    returns (JBBallotState ballotState)
  {
    // Get the funding cycle for the configuration in question.
    JBFundingCycle memory _fundingCycle = fundingCycleStore.get(_projectId, _configured);

    // Get the current ballot state.
    ballotState = finalState[_projectId][_configured];

    // If the final ballot state is still `Active`.
    if (ballotState == JBBallotState.Active) {
      ballotState = stateOf(_projectId, _configured, _fundingCycle.start);
      // If the ballot is active after the cycle has started, it should be finalized as failed.
      if (ballotState != JBBallotState.Active) {
        // Store the updated value.
        finalState[_projectId][_configured] = ballotState;

        emit Finalize(_projectId, _configured, ballotState, msg.sender);
      }
    }
  }
}

