    /// @dev Based on the insight that x^y = 2^(log2(x) * y).
    ///
    /// Requirements:
    /// - All from "exp2", "log2" and "mul".
    ///
    /// Caveats:
    /// - All from "exp2", "log2" and "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x Number to raise to given power y, as an unsigned 60.18-decimal fixed-point number.
    /// @param y Exponent to raise x to, as an unsigned 60.18-decimal fixed-point number.
    /// @return result x raised to power y, as an unsigned 60.18-decimal fixed-point number.
    function pow(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (x == 0) {
            result = y == 0 ? SCALE : uint256(0);
        } else {
            result = exp2(mul(log2(x), y));
        }
    }

    /// @notice Raises x (unsigned 60.18-decimal fixed-point number) to the power of y (basic unsigned integer) using the
    /// famous algorithm "exponentiation by squaring".
    ///
    /// @dev See https://en.wikipedia.org/wiki/Exponentiation_by_squaring
    ///
    /// Requirements:
    /// - The result must fit within MAX_UD60x18.
    ///
    /// Caveats:
    /// - All from "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x The base as an unsigned 60.18-decimal fixed-point number.
    /// @param y The exponent as an uint256.
    /// @return result The result as an unsigned 60.18-decimal fixed-point number.
    function powu(uint256 x, uint256 y) internal pure returns (uint256 result) {
        // Calculate the first iteration of the loop in advance.
        result = y & 1 > 0 ? x : SCALE;

        // Equivalent to "for(y /= 2; y > 0; y /= 2)" but faster.
        for (y >>= 1; y > 0; y >>= 1) {
            x = PRBMath.mulDivFixedPoint(x, x);

            // Equivalent to "y % 2 == 1" but faster.
            if (y & 1 > 0) {
                result = PRBMath.mulDivFixedPoint(result, x);
            }
        }
    }

    /// @notice Returns 1 as an unsigned 60.18-decimal fixed-point number.
    function scale() internal pure returns (uint256 result) {
        result = SCALE;
    }

    /// @notice Calculates the square root of x, rounding down.
    /// @dev Uses the Babylonian method https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method.
    ///
    /// Requirements:
    /// - x must be less than MAX_UD60x18 / SCALE.
    ///
    /// @param x The unsigned 60.18-decimal fixed-point number for which to calculate the square root.
    /// @return result The result as an unsigned 60.18-decimal fixed-point .
    function sqrt(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            if (x > MAX_UD60x18 / SCALE) {
                revert PRBMathUD60x18__SqrtOverflow(x);
            }
            // Multiply x by the SCALE to account for the factor of SCALE that is picked up when multiplying two unsigned
            // 60.18-decimal fixed-point numbers together (in this case, those two numbers are both the square root).
            result = PRBMath.sqrt(x * SCALE);
        }
    }

    /// @notice Converts a unsigned 60.18-decimal fixed-point number to basic integer form, rounding down in the process.
    /// @param x The unsigned 60.18-decimal fixed-point number to convert.
    /// @return result The same number in basic integer form.
    function toUint(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            result = x / SCALE;
        }
    }
}

contracts/system_tests/TestDistributeHel.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import '@paulrberg/contracts/math/PRBMath.sol';
import '@paulrberg/contracts/math/PRBMathUD60x18.sol';

import './helpers/TestBaseWorkflow.sol';

contract TestDistributeHeldFee is TestBaseWorkflow {
  JBController private _controller;
  JBETHPaymentTerminal private _terminal;
  JBTokenStore private _tokenStore;

  JBProjectMetadata private _projectMetadata;
  JBFundingCycleData private _data;
  JBFundingCycleMetadata private _metadata;
  JBGroupedSplits[] private _groupedSplits; // Default empty
  JBFundAccessConstraints[] private _fundAccessConstraints; // Default empty
  IJBPaymentTerminal[] private _terminals; // Default empty

  uint256 private _projectId;
  address private _projectOwner;
  uint256 private _weight = 1000 * 10**18;
  uint256 private _targetInWei = 10 * 10**18;

  function setUp() public override {
    super.setUp();

    _controller = jbController();
    _terminal = jbETHPaymentTerminal();
    _tokenStore = jbTokenStore();

    _projectMetadata = JBProjectMetadata({content: 'myIPFSHash', domain: 1});

    _data = JBFundingCycleData({
      duration: 14,
      weight: _weight,
      discountRate: 450000000,
      ballot: IJBFundingCycleBallot(address(0))
    });

    _metadata = JBFundingCycleMetadata({
      global: JBGlobalFundingCycleMetadata({allowSetTerminals: false, allowSetController: false}),
      reservedRate: 0,
      redemptionRate: 10000, //100%
      ballotRedemptionRate: 0,
      pausePay: false,
      pauseDistributions: false,
      pauseRedeem: false,
      pauseBurn: false,
      allowMinting: false,
      allowChangeToken: false,
      allowTerminalMigration: false,
      allowControllerMigration: false,
      holdFees: true,
      useTotalOverflowForRedemptions: false,
      useDataSourceForPay: false,
      useDataSourceForRedeem: false,
      dataSource: address(0)
    });

    _terminals.push(_terminal);

    _fundAccessConstraints.push(
      JBFundAccessConstraints({
        terminal: _terminal,
        token: jbLibraries().ETHToken(),
        distributionLimit: _targetInWei, // 10 ETH target
        overflowAllowance: 5 ether,
        distributionLimitCurrency: 1, // Currency = ETH
        overflowAllowanceCurrency: 1
      })
    );

    _projectOwner = multisig();

    _projectId = _controller.launchProjectFor(
      _projectOwner,
      _projectMetadata,
      _data,
      _metadata,
      block.timestamp,
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );
  }

  function testHeldFeeReimburse(
    uint256 payAmountInWei,
    uint16 fee,
    uint256 feeDiscount
  ) external {
    // Assuming we don't revert when distributing too much
    evm.assume(payAmountInWei <= _targetInWei);
    evm.assume(feeDiscount <= jbLibraries().MAX_FEE());
    evm.assume(fee <= 50_000_000); // fee cap
    address _userWallet = address(1234);

    evm.prank(multisig());
    _terminal.setFee(fee);

    IJBFeeGauge feeGauge = IJBFeeGauge(address(69696969));
    evm.etch(address(feeGauge), new bytes(0x1));
    evm.mockCall(
      address(feeGauge),
      abi.encodeWithSignature('currentDiscountFor(uint256)', _projectId),
      abi.encode(feeDiscount)
    );
    evm.prank(multisig());
    _terminal.setFeeGauge(feeGauge);

    uint256 discountedFee = fee - PRBMath.mulDiv(fee, feeDiscount, jbLibraries().MAX_FEE());

    // -- pay --
    _terminal.pay{value: payAmountInWei}(
      _projectId,
      payAmountInWei,
      address(0),
      /* _beneficiary */
      _userWallet,
      /* _minReturnedTokens */
      0,
      /* _preferClaimedTokens */
      false,
      /* _memo */
      'Take my money!',
      /* _delegateMetadata */
      new bytes(0)
    );

    // verify: beneficiary should have a balance of JBTokens
    uint256 _userTokenBalance = PRBMathUD60x18.mul(payAmountInWei, _weight);
    assertEq(_tokenStore.balanceOf(_userWallet, _projectId), _userTokenBalance);

    // verify: ETH balance in terminal should be up to date
    uint256 _terminalBalanceInWei = payAmountInWei;
    assertEq(jbPaymentTerminalStore().balanceOf(_terminal, _projectId), _terminalBalanceInWei);

    // -- distribute --
    _terminal.distributePayoutsOf(
      _projectId,
      payAmountInWei,
      jbLibraries().ETH(),
      address(0), //token (unused)
      /*min out*/
      0,
      /*LFG*/
      'lfg'
    );

    // verify: should have held the fee
    if (fee > 0 && payAmountInWei > 0) {
      assertEq(_terminal.heldFeesOf(_projectId)[0].fee, _terminal.fee());
      assertEq(_terminal.heldFeesOf(_projectId)[0].feeDiscount, feeDiscount);
      assertEq(_terminal.heldFeesOf(_projectId)[0].amount, payAmountInWei);
    }

    // -- add to balance --
    // Will get the fee reimbursed:
    uint256 heldFee = payAmountInWei -
      PRBMath.mulDiv(
        payAmountInWei,
        jbLibraries().MAX_FEE(),
        discountedFee + jbLibraries().MAX_FEE()
      ); // no discount
    uint256 balanceBefore = jbPaymentTerminalStore().balanceOf(_terminal, _projectId);
    _terminal.addToBalanceOf{value: payAmountInWei}(
      _projectId,
      payAmountInWei,
      address(0),
      'thanks for all the fish',
      /* _delegateMetadata */
      new bytes(0)
    );

    // verify: project should get the fee back (plus the addToBalance amount)
    assertEq(
      jbPaymentTerminalStore().balanceOf(_terminal, _projectId),
      balanceBefore + heldFee + payAmountInWei
    );
  }
}

contracts/system_tests/TestMultipleTermi.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.6;

import '@paulrberg/contracts/math/PRBMath.sol';
import './helpers/TestBaseWorkflow.sol';
import './mock/MockPriceFeed.sol';
import '@paulrberg/contracts/math/PRBMath.sol';

contract TestMultipleTerminals is TestBaseWorkflow {
  JBController controller;
  JBProjectMetadata _projectMetadata;
  JBFundingCycleData _data;
  JBFundingCycleMetadata _metadata;
  JBGroupedSplits[] _groupedSplits;
  JBFundAccessConstraints[] _fundAccessConstraints;

  IJBPaymentTerminal[] _terminals;
  JBERC20PaymentTerminal ERC20terminal;
  JBETHPaymentTerminal ETHterminal;

  JBTokenStore _tokenStore;
  address _projectOwner;

  address caller = address(6942069);

  uint256 FAKE_PRICE = 10;
  uint256 WEIGHT = 1000 * 10**18;
  uint256 projectId;

  function setUp() public override {
    super.setUp();
    evm.label(caller, 'caller');

    _groupedSplits.push();
    _groupedSplits[0].group = 1;
    _groupedSplits[0].splits.push(
      JBSplit({
        preferClaimed: false,
        preferAddToBalance: false,
        percent: jbLibraries().SPLITS_TOTAL_PERCENT(),
        projectId: 0,
        beneficiary: payable(caller),
        lockedUntil: 0,
        allocator: IJBSplitAllocator(address(0))
      })
    );

    _projectOwner = multisig();

    _tokenStore = jbTokenStore();

    controller = jbController();

    _projectMetadata = JBProjectMetadata({content: 'myIPFSHash', domain: 1});

    _data = JBFundingCycleData({
      duration: 14,
      weight: WEIGHT,
      discountRate: 450_000_000, // out of 1_000_000_000
      ballot: IJBFundingCycleBallot(address(0))
    });

    _metadata = JBFundingCycleMetadata({
      global: JBGlobalFundingCycleMetadata({allowSetTerminals: false, allowSetController: false}),
      reservedRate: 5000, //50%
      redemptionRate: 10000, //100%
      ballotRedemptionRate: 0,
      pausePay: false,
      pauseDistributions: false,
      pauseRedeem: false,
      pauseBurn: false,
      allowMinting: true,
      allowChangeToken: false,
      allowTerminalMigration: false,
      allowControllerMigration: false,
      holdFees: false,
      useTotalOverflowForRedemptions: true,
      useDataSourceForPay: false,
      useDataSourceForRedeem: false,
      dataSource: address(0)
    });

    ERC20terminal = new JBERC20PaymentTerminal(
      jbToken(),
      jbLibraries().USD(), // currency
      jbLibraries().ETH(), // base weight currency
      1, // JBSplitsGroupe
      jbOperatorStore(),
      jbProjects(),
      jbDirectory(),
      jbSplitsStore(),
      jbPrices(),
      jbPaymentTerminalStore(),
      multisig()
    );
    evm.label(address(ERC20terminal), 'JBERC20PaymentTerminalUSD');

    ETHterminal = jbETHPaymentTerminal();

    _fundAccessConstraints.push(
      JBFundAccessConstraints({
        terminal: ERC20terminal,
        token: address(jbToken()),
        distributionLimit: 10 * 10**18,
        overflowAllowance: 5 * 10**18,
        distributionLimitCurrency: jbLibraries().USD(),
        overflowAllowanceCurrency: jbLibraries().USD()
      })
    );

    _fundAccessConstraints.push(
      JBFundAccessConstraints({
        terminal: ETHterminal,
        token: jbLibraries().ETHToken(),
        distributionLimit: 10 * 10**18,
        overflowAllowance: 5 * 10**18,
        distributionLimitCurrency: jbLibraries().ETH(),
        overflowAllowanceCurrency: jbLibraries().ETH()
      })
    );

    _terminals.push(ERC20terminal);
    _terminals.push(ETHterminal);

    projectId = controller.launchProjectFor(
      _projectOwner,
      _projectMetadata,
      _data,
      _metadata,
      block.timestamp,
      _groupedSplits,
      _fundAccessConstraints,
      _terminals,
      ''
    );

    evm.startPrank(_projectOwner);
    MockPriceFeed _priceFeed = new MockPriceFeed(FAKE_PRICE);
    MockPriceFeed _priceFeedUsdEth = new MockPriceFeed(FAKE_PRICE);
    evm.label(address(_priceFeed), 'MockPrice Feed');

    jbPrices().addFeedFor(
      jbLibraries().USD(), // currency
      jbLibraries().ETH(), // base weight currency
      _priceFeedUsdEth
    );

    jbPrices().addFeedFor(
      jbLibraries().ETH(), // currency
      jbLibraries().USD(), // base weight currency
      _priceFeed
    );

    evm.stopPrank();
  }

  function testMultipleTerminal() public {
    // Send some token to the caller, so he can play
    evm.prank(_projectOwner);
    jbToken().transfer(caller, 20 * 10**18);

    // ---- Pay in token ----
    evm.prank(caller); // back to regular msg.sender (bug?)
    jbToken().approve(address(ERC20terminal), 20 * 10**18);
    evm.prank(caller); // back to regular msg.sender (bug?)
    ERC20terminal.pay(
      projectId,
      20 * 10**18,
      address(0),
      caller,
      0,
      false,
      'Forge test',
      new bytes(0)
    );

    // verify: beneficiary should have a balance of JBTokens (divided by 2 -> reserved rate = 50%)
    // price feed will return FAKE_PRICE*18 (for curr usd/base eth); since it's an 18 decimal terminal (ie calling getPrice(18) )
    uint256 _userTokenBalance = PRBMath.mulDiv(20 * 10**18, WEIGHT, 36 * FAKE_PRICE);
    assertEq(_tokenStore.balanceOf(caller, projectId), _userTokenBalance);

    // verify: balance in terminal should be up to date
    assertEq(jbPaymentTerminalStore().balanceOf(ERC20terminal, projectId), 20 * 10**18);

    // ---- Pay in ETH ----
    address beneficiaryTwo = address(696969);
    ETHterminal.pay{value: 20 ether}(
      projectId,
      20 ether,
      address(0),
      beneficiaryTwo,
      0,
      false,
      'Forge test',
      new bytes(0)
    ); // funding target met and 10 ETH are now in the overflow

    // verify: beneficiary should have a balance of JBTokens (divided by 2 -> reserved rate = 50%)
    uint256 _userEthBalance = PRBMath.mulDiv(20 ether, (WEIGHT / 10**18), 2);
    assertEq(_tokenStore.balanceOf(beneficiaryTwo, projectId), _userEthBalance);

    // verify: ETH balance in terminal should be up to date
    assertEq(jbPaymentTerminalStore().balanceOf(ETHterminal, projectId), 20 ether);

    // ---- Use allowance ----
    evm.startPrank(_projectOwner);
    ERC20terminal.useAllowanceOf(
      projectId,
      5 * 10**18, // amt in ETH (overflow allowance currency is in ETH)
      jbLibraries().USD(), // Currency -> (fake price is 10)
      address(0), //token (unused)
      1, // Min wei out
      payable(msg.sender), // Beneficiary
      'MEMO'
    );
    evm.stopPrank();

    // Funds leaving the contract -> take the fee
    assertEq(
      jbToken().balanceOf(msg.sender),
      PRBMath.mulDiv(
        5 * 10**18,
        jbLibraries().MAX_FEE(),
        jbLibraries().MAX_FEE() + ERC20terminal.fee()
      )
    );

    // Distribute the funding target ETH
    uint256 initBalance = caller.balance;
    evm.prank(_projectOwner);
    ETHterminal.distributePayoutsOf(
      projectId,
      10 * 10**18,
      jbLibraries().ETH(), // Currency
      address(0), //token (unused)
      0, // Min wei out
      'Foundry payment' // Memo
    );
    // Funds leaving the ecosystem -> fee taken
    assertEq(
      caller.balance,
      initBalance +
        PRBMath.mulDiv(
          10 * 10**18,
          jbLibraries().MAX_FEE(),
          ETHterminal.fee() + jbLibraries().MAX_FEE()
        )
    );

    // redeem eth from the overflow by the token holder:
    uint256 totalSupply = jbController().totalOutstandingTokensOf(projectId, 5000);
    uint256 overflow = jbPaymentTerminalStore().currentTotalOverflowOf(projectId, 18, 1);

    uint256 callerEthBalanceBefore = caller.balance;

    evm.prank(caller);
    ETHterminal.redeemTokensOf(
      caller,
      projectId,
      100_000,
      address(0), //token (unused)
      0,
      payable(caller),
      'gimme my money back',
      new bytes(0)
    );

    assertEq(caller.balance, callerEthBalanceBefore + ((100_000 * overflow) / totalSupply));
  }
}

