pragma solidity =0.8.7;

import {StableMath} from "../../shared/StableMath.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @dev Implementation of the `IERC20` interface.
 *
 * This implementation is agnostic to the way tokens are created. This means
 * that a supply mechanism has to be added in a derived contract using `_mint`.
 * For a generic mechanism see `ERC20Mintable`.
 *
 * *For a detailed writeup see our guide [How to implement supply
 * mechanisms](https://forum.zeppelin.solutions/t/how-to-implement-erc20-supply-mechanisms/226).*
 *
 * We have followed general OpenZeppelin guidelines: functions revert instead
 * of returning `false` on failure. This behavior is nonetheless conventional
 * and does not conflict with the expectations of ERC20 applications.
 *
 * Additionally, an `Approval` event is emitted on calls to `transferFrom`.
 * This allows applications to reconstruct the allowance for all accounts just
 * by listening to said events. Other implementations of the EIP may not emit
 * these events, as it isn't required by the specification.
 *
 * Finally, the non-standard `decreaseAllowance` and `increaseAllowance`
 * functions have been added to mitigate the well-known issues around setting
 * allowances. See `IERC20.approve`.
 */
contract ERC20WithFee is IERC20 {
  using StableMath for uint256;

  uint256 public feeRate;

  mapping(address => uint256) private _balances;

  mapping(address => mapping(address => uint256)) private _allowances;

  uint256 private _totalSupply;

  string private _name;
  string private _symbol;
  uint8 private _decimals;

  event FeePaid(address indexed sender, uint256 amount);

  /**
   * @dev Sets the values for `name`, `symbol`, and `decimals`. All three of
   * these values are immutable: they can only be set once during
   * construction.
   */
  function _initialize(
    string memory nameArg,
    string memory symbolArg,
    uint8 decimalsArg
  ) internal {
    _name = nameArg;
    _symbol = symbolArg;
    _decimals = decimalsArg;
  }

  /**
   * @dev Returns the name of the token.
   */
  function name() public view returns (string memory) {
    return _name;
  }

  /**
   * @dev Returns the symbol of the token, usually a shorter version of the
   * name.
   */
  function symbol() public view returns (string memory) {
    return _symbol;
  }

  /**
   * @dev Returns the number of decimals used to get its user representation.
   * For example, if `decimals` equals `2`, a balance of `505` tokens should
   * be displayed to a user as `5,05` (`505 / 10 ** 2`).
   *
   * Tokens usually opt for a value of 18, imitating the relationship between
   * Ether and Wei.
   *
   * > Note that this information is only used for _display_ purposes: it in
   * no way affects any of the arithmetic of the contract, including
   * `IERC20.balanceOf` and `IERC20.transfer`.
   */
  function decimals() public view returns (uint8) {
    return _decimals;
  }

  /**
   * @dev See `IERC20.totalSupply`.
   */
  function totalSupply() public view override returns (uint256) {
    return _totalSupply;
  }

  /**
   * @dev See `IERC20.balanceOf`.
   */
  function balanceOf(address account) public view override returns (uint256) {
    return _balances[account];
  }

  /**
   * @dev See `IERC20.transfer`.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount)
    public
    override
    returns (bool)
  {
    _transfer(msg.sender, recipient, amount);
    return true;
  }

  /**
   * @dev See `IERC20.allowance`.
   */
  function allowance(address owner, address spender)
    public
    view
    override
    returns (uint256)
  {
    return _allowances[owner][spender];
  }

  /**
   * @dev See `IERC20.approve`.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 value)
    public
    override
    returns (bool)
  {
    _approve(msg.sender, spender, value);
    return true;
  }

  /**
   * @dev See `IERC20.transferFrom`.
   *
   * Emits an `Approval` event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of `ERC20`;
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `value`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(
    address sender,
    address recipient,
    uint256 amount
  ) public override returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to `approve` that can be used as a mitigation for
   * problems described in `IERC20.approve`.
   *
   * Emits an `Approval` event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue)
    public
    returns (bool)
  {
    _approve(
      msg.sender,
      spender,
      _allowances[msg.sender][spender] + addedValue
    );
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to `approve` that can be used as a mitigation for
   * problems described in `IERC20.approve`.
   *
   * Emits an `Approval` event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue)
    public
    returns (bool)
  {
    _approve(
      msg.sender,
      spender,
      _allowances[msg.sender][spender] - subtractedValue
    );
    return true;
  }

  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to `transfer`, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a `Transfer` event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
  function _transfer(
    address sender,
    address recipient,
    uint256 amount
  ) internal {
    require(sender != address(0), "ERC20: transfer from the zero address");
    require(recipient != address(0), "ERC20: transfer to the zero address");

    uint256 fee = amount.mulTruncate(feeRate);
    uint256 remainder = amount - fee;

    _burn(sender, fee);

    _balances[sender] = _balances[sender] - remainder;
    _balances[recipient] = _balances[recipient] + remainder;
    emit Transfer(sender, recipient, remainder);
  }

  /** @dev Creates `amount` tokens and assigns them to `account`, increasing
   * the total supply.
   *
   * Emits a `Transfer` event with `from` set to the zero address.
   *
   * Requirements
   *
   * - `to` cannot be the zero address.
   */
  function _mint(address account, uint256 amount) internal {
    require(account != address(0), "ERC20: mint to the zero address");

    _totalSupply = _totalSupply + amount;
    _balances[account] = _balances[account] + amount;
    emit Transfer(address(0), account, amount);
  }

  /**
   * @dev Destoys `amount` tokens from `account`, reducing the
   * total supply.
   *
   * Emits a `Transfer` event with `to` set to the zero address.
   *
   * Requirements
   *
   * - `account` cannot be the zero address.
   * - `account` must have at least `amount` tokens.
   */
  function _burn(address account, uint256 value) internal {
    require(account != address(0), "ERC20: burn from the zero address");

    _totalSupply = _totalSupply - value;
    _balances[account] = _balances[account] - value;
    emit Transfer(account, address(0), value);
  }

  /**
   * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
   *
   * This is internal function is equivalent to `approve`, and can be used to
   * e.g. set automatic allowances for certain subsystems, etc.
   *
   * Emits an `Approval` event.
   *
   * Requirements:
   *
   * - `owner` cannot be the zero address.
   * - `spender` cannot be the zero address.
   */
  function _approve(
    address owner,
    address spender,
    uint256 value
  ) internal {
    require(owner != address(0), "ERC20: approve from the zero address");
    require(spender != address(0), "ERC20: approve to the zero address");

    _allowances[owner][spender] = value;
    emit Approval(owner, spender, value);
  }

  /**
   * @dev Destoys `amount` tokens from `account`.`amount` is then deducted
   * from the caller's allowance.
   *
   * See `_burn` and `_approve`.
   */
  function _burnFrom(address account, uint256 amount) internal {
    _burn(account, amount);
    _approve(account, msg.sender, _allowances[account][msg.sender] - amount);
  }

  /**
   * @dev See `ERC20._mint`.
   *
   * Requirements:
   *
   * - the caller must have the `MinterRole`.
   */
  function mint(address account, uint256 amount) public returns (bool) {
    _mint(account, amount);
    return true;
  }
}

contract MockERC20WithFee is ERC20WithFee {
  constructor(
    string memory _name,
    string memory _symbol,
    uint8 _decimals,
    address _initialRecipient,
    uint256 _initialMint
  ) {
    ERC20WithFee._initialize(_name, _symbol, _decimals);
    feeRate = 1e15;
    _mint(_initialRecipient, _initialMint * (10**uint256(_decimals)));
  }
}

contracts/ppo-staking/shared/StableMath..content:
// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity =0.8.7;

/**
 * @title   StableMath
 * @author  mStable
 * @notice  A library providing safe mathematical operations to multiply and
 *          divide with standardised precision.
 * @dev     Derives from OpenZeppelin's SafeMath lib and uses generic system
 *          wide variables for managing precision.
 */
library StableMath {
  /**
   * @dev Scaling unit for use in specific calculations,
   * where 1 * 10**18, or 1e18 represents a unit '1'
   */
  uint256 private constant FULL_SCALE = 1e18;

  /**
   * @dev Token Ratios are used when converting between units of bAsset, mAsset and MTA
   * Reasoning: Takes into account token decimals, and difference in base unit (i.e. grams to Troy oz for gold)
   * bAsset ratio unit for use in exact calculations,
   * where (1 bAsset unit * bAsset.ratio) / ratioScale == x mAsset unit
   */
  uint256 private constant RATIO_SCALE = 1e8;

  /**
   * @dev Provides an interface to the scaling unit
   * @return Scaling unit (1e18 or 1 * 10**18)
   */
  function getFullScale() internal pure returns (uint256) {
    return FULL_SCALE;
  }

  /**
   * @dev Provides an interface to the ratio unit
   * @return Ratio scale unit (1e8 or 1 * 10**8)
   */
  function getRatioScale() internal pure returns (uint256) {
    return RATIO_SCALE;
  }

  /**
   * @dev Scales a given integer to the power of the full scale.
   * @param x   Simple uint256 to scale
   * @return    Scaled value a to an exact number
   */
  function scaleInteger(uint256 x) internal pure returns (uint256) {
    return x * FULL_SCALE;
  }

  /***************************************
              PRECISE ARITHMETIC
    ****************************************/

  /**
   * @dev Multiplies two precise units, and then truncates by the full scale
   * @param x     Left hand input to multiplication
   * @param y     Right hand input to multiplication
   * @return      Result after multiplying the two inputs and then dividing by the shared
   *              scale unit
   */
  function mulTruncate(uint256 x, uint256 y) internal pure returns (uint256) {
    return mulTruncateScale(x, y, FULL_SCALE);
  }

  /**
   * @dev Multiplies two precise units, and then truncates by the given scale. For example,
   * when calculating 90% of 10e18, (10e18 * 9e17) / 1e18 = (9e36) / 1e18 = 9e18
   * @param x     Left hand input to multiplication
   * @param y     Right hand input to multiplication
   * @param scale Scale unit
   * @return      Result after multiplying the two inputs and then dividing by the shared
   *              scale unit
   */
  function mulTruncateScale(
    uint256 x,
    uint256 y,
    uint256 scale
  ) internal pure returns (uint256) {
    // e.g. assume scale = fullScale
    // z = 10e18 * 9e17 = 9e36
    // return 9e36 / 1e18 = 9e18
    return (x * y) / scale;
  }

  /**
   * @dev Multiplies two precise units, and then truncates by the full scale, rounding up the result
   * @param x     Left hand input to multiplication
   * @param y     Right hand input to multiplication
   * @return      Result after multiplying the two inputs and then dividing by the shared
   *              scale unit, rounded up to the closest base unit.
   */
  function mulTruncateCeil(uint256 x, uint256 y)
    internal
    pure
    returns (uint256)
  {
    // e.g. 8e17 * 17268172638 = 138145381104e17
    uint256 scaled = x * y;
    // e.g. 138145381104e17 + 9.99...e17 = 138145381113.99...e17
    uint256 ceil = scaled + FULL_SCALE - 1;
    // e.g. 13814538111.399...e18 / 1e18 = 13814538111
    return ceil / FULL_SCALE;
  }

  /**
   * @dev Precisely divides two units, by first scaling the left hand operand. Useful
   *      for finding percentage weightings, i.e. 8e18/10e18 = 80% (or 8e17)
   * @param x     Left hand input to division
   * @param y     Right hand input to division
   * @return      Result after multiplying the left operand by the scale, and
   *              executing the division on the right hand input.
   */
  function divPrecisely(uint256 x, uint256 y) internal pure returns (uint256) {
    // e.g. 8e18 * 1e18 = 8e36
    // e.g. 8e36 / 10e18 = 8e17
    return (x * FULL_SCALE) / y;
  }

  /***************************************
                  RATIO FUNCS
    ****************************************/

  /**
   * @dev Multiplies and truncates a token ratio, essentially flooring the result
   *      i.e. How much mAsset is this bAsset worth?
   * @param x     Left hand operand to multiplication (i.e Exact quantity)
   * @param ratio bAsset ratio
   * @return c    Result after multiplying the two inputs and then dividing by the ratio scale
   */
  function mulRatioTruncate(uint256 x, uint256 ratio)
    internal
    pure
    returns (uint256 c)
  {
    return mulTruncateScale(x, ratio, RATIO_SCALE);
  }

  /**
   * @dev Multiplies and truncates a token ratio, rounding up the result
   *      i.e. How much mAsset is this bAsset worth?
   * @param x     Left hand input to multiplication (i.e Exact quantity)
   * @param ratio bAsset ratio
   * @return      Result after multiplying the two inputs and then dividing by the shared
   *              ratio scale, rounded up to the closest base unit.
   */
  function mulRatioTruncateCeil(uint256 x, uint256 ratio)
    internal
    pure
    returns (uint256)
  {
    // e.g. How much mAsset should I burn for this bAsset (x)?
    // 1e18 * 1e8 = 1e26
    uint256 scaled = x * ratio;
    // 1e26 + 9.99e7 = 100..00.999e8
    uint256 ceil = scaled + RATIO_SCALE - 1;
    // return 100..00.999e8 / 1e8 = 1e18
    return ceil / RATIO_SCALE;
  }

  /**
   * @dev Precisely divides two ratioed units, by first scaling the left hand operand
   *      i.e. How much bAsset is this mAsset worth?
   * @param x     Left hand operand in division
   * @param ratio bAsset ratio
   * @return c    Result after multiplying the left operand by the scale, and
   *              executing the division on the right hand input.
   */
  function divRatioPrecisely(uint256 x, uint256 ratio)
    internal
    pure
    returns (uint256 c)
  {
    // e.g. 1e14 * 1e8 = 1e22
    // return 1e22 / 1e12 = 1e10
    return (x * RATIO_SCALE) / ratio;
  }

  /***************************************
                    HELPERS
    ****************************************/

  /**
   * @dev Calculates minimum of two numbers
   * @param x     Left hand input
   * @param y     Right hand input
   * @return      Minimum of the two inputs
   */
  function min(uint256 x, uint256 y) internal pure returns (uint256) {
    return x > y ? y : x;
  }

  /**
   * @dev Calculated maximum of two numbers
   * @param x     Left hand input
   * @param y     Right hand input
   * @return      Maximum of the two inputs
   */
  function max(uint256 x, uint256 y) internal pure returns (uint256) {
    return x > y ? x : y;
  }

  /**
   * @dev Clamps a value to an upper bound
   * @param x           Left hand input
   * @param upperBound  Maximum possible value to return
   * @return            Input x clamped to a maximum value, upperBound
   */
  function clamp(uint256 x, uint256 upperBound)
    internal
    pure
    returns (uint256)
  {
    return x > upperBound ? upperBound : x;
  }
}

